mapper.select:
select
transfersOrderId, pId, unitrequire, num, outWare,
inWare,stuta,createtime,createPerson,
details
from
clothesppsdb.transfersorder


		and transfersOrderId = #{transfersorderid}


		and pId = #{pid}


		and unitrequire = #{unitrequire}


		and num = #{num}


		and outWare = #{outware}


		and inWare = #{inware}


		and stuta = #{stuta}


		and createtime = #{createtime}


		and createPerson = #{createPerson}


		and details = #{details}

mapper.update:
update clothesppsdb.transfersorder


		pId = #{pid},


		unitrequire = #{unitrequire},


		num = #{num},


		outWare = #{outware},


		inWare = #{inware},


		and stuta = #{stuta}


		and createtime = #{createtime}


		and createPerson = #{createPerson}


		details = #{details},


where transfersOrderId = #{transfersorderid}
